document.addEventListener("alpine:init",()=>{Alpine.data("nav",()=>({show:!1,init(){document.getElementById("hello-banner").offsetHeight===0&&(this.show=!0)},handleScroll(){this.show=window.scrollY>=document.getElementById("hello-banner").offsetHeight}}))}),document.addEventListener("alpine:init",()=>{Alpine.data("home",()=>({face:"neutral",init(){},eyeballtracking(e){const t=document.getElementById("face-container");e.clientY<100?this.face="above":e.clientY>t.clientHeight*1.2?this.face="below":this.face="neutral",document.querySelectorAll("#hello-banner .eyes").forEach(t=>{const n=t.getBoundingClientRect(),o=n.left+n.width/2,i=n.top+n.height/2,s=Math.atan2(e.clientX-o,e.clientY-i),a=Math.sin(s)*8,r=Math.cos(s)*8;t.style.transform=`translate(${a}px, ${r}px)`})}}))})