window.onload=function(){init()};function init(){const a=document.querySelectorAll('#filter input[type="checkbox"]');a.forEach(function(a){a.checked=!1,a.addEventListener('change',function(){revalidate()})})}function revalidate(){let a=[];document.querySelectorAll('#filter [data-field="techs"] input:checked').forEach(function(b){a.push(b.name)});let b=[];document.querySelectorAll('#filter [data-field="types"] input:checked').forEach(function(a){b.push(a.name)});const c=document.querySelector('#filter input[name="has-doc"]'),d=document.querySelector('#filter input[name="has-access"]'),e=document.querySelector('#filter input[name="has-demo"]'),f=document.querySelector('#filter input[name="has-download"]'),g=document.querySelector('#filter input[name="wip"]');projects().forEach(function(h){const t=JSON.parse(h.dataset.techs),p=h.dataset.type,k=h.dataset.hasdoc==='true',l=h.dataset.hasaccess==='true',m=h.dataset.hasdemo==='true',n=h.dataset.hasdownload==='true',o=h.dataset.wip==='true',j=t.findIndex(b=>a.includes(b))>=0,q=!!!a.length||j,r=!!!b.length||b.includes(p),s=!g.checked||o,i=c.checked||e.checked||f.checked||d.checked,u=!(i||c.checked)||c.checked&&k,v=!(i||e.checked)||e.checked&&m,w=!(i||f.checked)||f.checked&&n,x=!(i||d.checked)||d.checked&&l;q&&r&&s&&(u||v||w||x)?showProject(h):hideProject(h)})}function projects(){return document.querySelectorAll('#projects > li')}function hideProject(a){a.classList.add("not-filtered")}function showProject(a){a.classList.remove("not-filtered")}