window.onload=function(){init()};function init(){const a=document.querySelectorAll('#filter input[type="checkbox"]');a.forEach(function(a){a.checked=!1,a.addEventListener('change',function(){revalidate()})})}function revalidate(){let a=[];document.querySelectorAll('#filter [data-field="techs"] input:checked').forEach(function(b){a.push(b.name)});let b=[];document.querySelectorAll('#filter [data-field="types"] input:checked').forEach(function(a){b.push(a.name)});const c=document.querySelector('#filter input[name="has-doc"]'),d=document.querySelector('#filter input[name="has-demo"]'),e=document.querySelector('#filter input[name="has-download"]'),f=document.querySelector('#filter input[name="wip"]');projects().forEach(function(g){const r=JSON.parse(g.dataset.techs),n=g.dataset.type,j=g.dataset.hasdoc==='true',k=g.dataset.hasdemo==='true',l=g.dataset.hasdownload==='true',m=g.dataset.wip==='true',i=r.findIndex(b=>a.includes(b))>=0,o=!!!a.length||i,p=!!!b.length||b.includes(n),q=!f.checked||m,h=c.checked||d.checked||e.checked,s=!(h||c.checked)||c.checked&&j,t=!(h||d.checked)||d.checked&&k,u=!(h||e.checked)||e.checked&&l;o&&p&&q&&(s||t||u)?showProject(g):hideProject(g)})}function projects(){return document.querySelectorAll('#projects > li')}function hideProject(a){a.classList.add("not-filtered")}function showProject(a){a.classList.remove("not-filtered")}