changeTheme(localStorage.theme==="dark"||!("theme"in localStorage)&&window.matchMedia("(prefers-color-scheme: dark)").matches?"dark":"light");function changeTheme(e){e=="dark"?(localStorage.theme="dark",document.documentElement.classList.add("dark")):(localStorage.theme="light",document.documentElement.classList.remove("dark"))}function toggleTheme(){changeTheme(localStorage.theme=="dark"?"light":"dark")}