localStorage.theme==='dark'||!('theme'in localStorage)&&window.matchMedia('(prefers-color-scheme: dark)').matches?changeTheme('dark'):changeTheme('light');function changeTheme(a){a=='dark'?(localStorage.theme='dark',document.documentElement.classList.add('dark')):(localStorage.theme='light',document.documentElement.classList.remove('dark'))}function toggleTheme(){changeTheme(localStorage.theme=='dark'?'light':'dark')}